select T.name, P.name, count(*) as cnt from Trainer as T join CatchedPokemon as CP on T.id = CP.owner_id join Pokemon as P on CP.pid = P.id where T.id in (select Trainer.id from Trainer join CatchedPokemon on Trainer.id = CatchedPokemon.owner_id join Pokemon on Pokemon.id = CatchedPokemon.pid group by Trainer.id having count(distinct Pokemon.type) = 1) group by T.id, P.id order by T.name;
